import { Link } from "react-router-dom";
import Button from "./Button.js";

export default function Product(props) {
  const { details } = props;

  // Check if product is already in cart
  const productFromCart = props.cart.find(
    (product) => product.id === details.id
  );
  // get product quantity from cart
  const quantity = productFromCart ? productFromCart.quantity : 0;

  console.log("Image:")
  console.log(details.image)
  // console.log(details.image[Object.keys(details.image)[0]])

  return (
    <>
      <div className="product-image-container">
        <Link to={`/products/${details.id}`}>
          <img
            src={details.image}
            width="100"
            height="100"
            className="product-image"
            alt={details.name}
          />
        </Link>
      </div>
      <div className="product">
        <div className="product-info">
          <h3>{details.name}</h3>
          {/* <p>{details.description}</p> */}
        </div>
        <div className="product-checkout">
          <div>
            {quantity > 0 && (
              <div className="product-quantity-container">
                <div className="product-quantity">{quantity}</div>
              </div>
            )}
            {quantity > 0 && (
              <Button
                outline
                onClick={() => props.onProductDelete(details.id)}
                className="product-delete"
              >
                x
              </Button>
            )}
          </div>
          <Button outline onClick={() => props.onProductAdd(details)}>
            {/* ${details.price} */}
            Add to cart
          </Button>
        </div>
      </div>
    </>
  );
}
