import { useState } from "react";
import { loadStripe } from "@stripe/stripe-js";
import Input from "./Input.js";
import Button from "./Button.js";

// TODO: Replace with your own publishable key
const stripeLoadedPromise = loadStripe("pk_test_51LQtUyLacYG8sMVCpQIHpTdGa5JsE8ogoGplCN5gsepXCzSBRvF2wej4c8bKFXc6SxO39lUr9dcjIekZz1uzHsmS00Fi4NhbR3");

export default function Cart(props) {
  const [email, setEmail] = useState("");
  const [product, setProduct] = useState({});
  const { cart, onProductAdd, onProductMinus, onProductDelete } = props;


  const totalPrice = cart.reduce(
    (total, product) => total + product.price * product.quantity,
    0
  );

  // const existingProduct = cart.find(
  //   (product) => product.id === newProduct.id
  // );

  // // store new price_ids from stripe
  // const newPriceId = [
  //   "price_1LRAXVLacYG8sMVCJxtrGZmc",
  //   "price_1LRAYWLacYG8sMVC8KC2jIOT",
  //   "price_1LRAdaLacYG8sMVCwbD8iu2B",
  //   "price_1LRAeMLacYG8sMVCfof1RPG4"
  // ]

  function handleFormSubmit(event) {
    event.preventDefault();

    const lineItems = cart.map((product, i) => {
      // // overwrite price_ids from get request
      // // console.log(i)
      // // console.log(newPriceId[i])
      // product = {...product, price_id: newPriceId[i]}
      // // console.log(product)
      return { price: product.price_id, quantity: product.quantity };
    });

    console.log(lineItems)

    stripeLoadedPromise.then((stripe) => {
      stripe
        .redirectToCheckout({
          lineItems: lineItems,
          mode: "payment",
          successUrl: "http://localhost:3000/cart",
          cancelUrl: "http://localhost:3000/cart",
          customerEmail: email,
        })
        .then((response) => {
          // this will only log if the redirect did not work
          console.log(response.error);
        })
        .catch((error) => {
          // wrong API key? you will see the error message here
          console.log(error);
        });
    });
  }

  return (
    <div className="cart-layout">
      <div>
        <h1>Your Cart</h1>
        {cart.length === 0 && (
          <p>You have not added any product to your cart yet.</p>
        )}
        {cart.length > 0 && (
          <>
            <table className="table table-cart">
              <thead>
                <tr>
                  <th width="25%" className="th-product">
                    Product
                  </th>
                  <th width="20%">Unit price</th>
                  <th width="10%">Quanity</th>
                  <th width="25%">Total</th>
                </tr>
              </thead>
              <tbody>
                {cart.map((product) => {
                  return (
                    <tr key={product.id}>
                      <td>
                        <img
                          src={product.image}
                          width="45"
                          height="45"
                          alt=""
                        />{" "}
                        {product.name}
                      </td>
                      <td>${product.price}</td>
                      <td>
                        <Button
                          circle
                          onClick={() => props.onProductMinus(product)}
                        >
                          -
                        </Button>
                        {product.quantity}
                        <Button
                          circle
                          onClick={() => props.onProductPlus(product)}
                        >
                          +
                        </Button>
                      </td>
                      <td>
                        <strong>
                          ${product.price * product.quantity}
                          <Button
                            circle
                            onClick={() => props.onProductDelete(product.id)}
                          >
                            ×
                          </Button>
                        </strong>
                      </td>
                    </tr>
                  );
                })}
              </tbody>
              <tfoot>
                <tr>
                  <th colSpan="2"></th>
                  <th className="cart-highlight">Total</th>
                  <th className="cart-highlight">${totalPrice}</th>
                </tr>
              </tfoot>
            </table>
            <form className="pay-form" onSubmit={handleFormSubmit}>
              <p>
                Enter your email, click on pay and your products will be
                delivered to you on the same day! 🎉
              </p>
              <Input
                placeholder="Email"
                onChange={(event) => setEmail(event.target.value)}
                value={email}
                type="email"
                required
              />
              <Button type="submit">Pay</Button>
            </form>
          </>
        )}
      </div>
    </div>
  );
}
