import { useState, useEffect } from "react";
import {
  NavLink,
  Link,
  Switch,
  Route,
  useParams,
  useRouteMatch,
} from "react-router-dom";
// import useFetch from "./useFetch.js";
import ProductDetailInfo from "./ProductDetailInfo.js";
import ProductDetailStorage from "./ProductDetailStorage.js";
import productsObject from "./ProductsObject.js";
import Button from "./Button.js";

export default function ProductDetails(props) {
  const [product, setProduct] = useState({});
  const params = useParams();
  const match = useRouteMatch();
  // const { get } = useFetch("https://react-tutorial-demo.firebaseio.com/");

  // useEffect(() => {
  //   get(`productinfo/id${params.id}.json`)
  //     .then((data) => {
  //       setProduct(data);
  //     })
  //     .catch((error) => console.log("Could not load product details", error));
  // }, []);

  // console.log("PRODUCTS:")
  // console.log(products)
  // console.log(productsObject)

  useEffect(() => {
    setProduct(productsObject[params.id - 1])
  }, [])

  return (
    <div className="product-details-layout">
      <Link to="/products">back</Link>
      <div className="product-details-left">
        <img
          src={product.image}
          className="product-details-image"
          alt={product.name}
          />
      </div>
      <div className="product-details-right">
        <h2>{product.name}</h2>
        <h3>â‚¬{product.price}</h3>
        <Button onClick={() => props.onProductPlus(product)}>Add to cart</Button>
        <div className="tabs">
          <ul>
            <li>
              <NavLink exact activeClassName="tab-active" to={match.url}>
                Details
              </NavLink>
            </li>
            <li>
              <NavLink
                exact
                activeClassName="tab-active"
                to={match.url + "/delivery"}
              >
                Delivery
              </NavLink>
            </li>
          </ul>
        </div>
        <Switch>
          <Route exact path={match.path}>
            <ProductDetailInfo
              onProductPlus={props.onProductPlus}
              product={product}
            />
          </Route>

          <Route path={match.path + "/delivery"}>
            <ProductDetailStorage storage={product.storage} />
          </Route>
        </Switch>
      </div>
    </div>
  );
}
